import Radio from './plugins/radio' //发布广播
import components from './components' //组件包
import directives from './directives' //自定义指令包
import filters from './filters' //自定义指令包
import plugins from './plugins' //自定义指令包

export default {
    install(Vue, opts = {}) {

        //将所有的组件注册到全局
        components.map(component => Vue.component(component.name, component));

        //将所有的指令注册到全局
        directives.map(directive => {Vue.directive(directive.name, directive)});

        //注册全局过滤器
        filters.map(filter => Vue.filter(filter.name, filter));

        plugins.map(plugin => Vue.use(plugin));

    }
}